import React, { Component } from "react";
import axios from "axios";
import utils from "../src/utils";

export default class Index extends Component {
  constructor(props) {
    super(props);
    this.state = { movies: {}, loading: false, value: "" };
    this.queryForMovies = this.queryForMovies.bind(this);
    this.apiKey = "e3963faaff14ce2935d481ee7d0179fb";
  }

  async queryForMovies(queryText) {
    console.log(queryText);
    this.setState({ value: queryText, loading: true });
    this.url = `https://api.themoviedb.org/3/search/movie?query=${queryText}&api_key=${this.apiKey}`;
    let cancel;
    if (cancel) {
      cancel.cancel();
    }
    cancel = axios.CancelToken.source();
    try {
      const response = await axios(this.url, { cancelToken: cancel.token });
      const movies = await response.data.results;
      console.log(movies);
    } catch (error) {
      if (axios.isCancel(error)) {
        console.log("Request canceled", error.message);
      } else {
        console.log("Something went rong", error.message);
      }
    }
  }
  render() {
    return (
      <div>
        <input
          type="text"
          placeholder="Search for..."
          value={this.state.value}
          onChange={event => this.queryForMovies(event.target.value)}
        />
      </div>
    );
  }
}
